<textarea <?php $tmp = @$data->id; if (isset($data->id) && $data->id !== "" && $data->id !== false) {echo 'id="'.$tmp.'"'; } ?> <?php $tmp = @$data->name; if (isset($data->name) && $data->name !== "" && $data->name !== false) {echo 'name="'.$tmp.'"'; } ?> <?php $tmp = @$data->rows; if (isset($data->rows) && $data->rows !== "" && $data->rows !== false) {echo 'rows="'.$tmp.'"'; } ?> <?php $tmp = @$data->cols; if (isset($data->cols) && $data->cols !== "" && $data->cols !== false) {echo 'cols="'.$tmp.'"'; } ?> style="width: 100%; height: <?php echo @$data->height;?>"><?php echo htmlspecialchars(@$data->value) ?></textarea>